import React, { Component } from 'react';
import Paragraph from './components/Paragraph';
import Title from './components/Title';

class App extends Component {
  constructor() {
    super()
    this.state = {
      angka1: 7,
      angka2: 5,
      hasil: 0
    }
  }

  _handleClick = () => {
    const perhitungan = this.state.angka1 + this.state.angka2
    this.setState({hasil: perhitungan})
  }

  render() {
    return (
      <div>
        <Title/>
        <Paragraph></Paragraph>
        <input value = { this.state.angka1 }></input>
        <input value = { this.state.angka2 }></input>
        <button onClick = { this._handleClick } >HITUNG</button>
        <h1>Hasil : { this.state.hasil }</h1>
      </div>
    );
  }
}

export default App;
