import React, { Component } from 'react';
import axios from 'axios';

class KonstituenCreate extends Component {
  constructor() {
    super()

    this.state = {
      nama : '',
      nik : '',
      hp : '',
      alamat : '',
      kecamatanID : '',
      kelurahanID : '',
      tps : ''
    }
  }

  _handleSubmit = (e) => {
    const { nama, nik, hp, alamat, kecamatanID, kelurahanID, tps } = this.state
    axios.post('http://localhost:5000/konstituens', { nama, nik, hp,  alamat, kecamatanID, kelurahanID, tps})
      .then(res => {
        console.log(res)
      }).catch(err => console.log(err))

    // Untuk mencegah Submit merefresh page, karena default habit nya submit akan langsung merefresh page  
    e.preventDefault()
  }

  _handleChange = (e) => {
    this.setState({ [e.target.name] : e.target.value })
  }

  render() {
    return (
        <div>
          <h1>Tambah Konstituen</h1>
          <form onSubmit = { this._handleSubmit }>
            <div className="form-group">
              <label>Nama</label>
              <input 
                name="nama" 
                value = {this.state.nama} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Nomor Induk Kependudukan</label>
              <input 
                name="nik" 
                value = {this.state.nik} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Nomor Handphone</label>
              <input 
                name="hp" 
                value = {this.state.hp} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Alamat</label>
              <input 
                name="alamat" 
                value = {this.state.alamat} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Kecamatan</label>
              <input 
                name="kecamatan" 
                value = {this.state.kecamatanID} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Kelurahan</label>
              <input 
                name="kelurahan" 
                value = {this.state.kelurahanID} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Nomor TPS</label>
              <input 
                name="tps" 
                value = {this.state.tps} onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <button type="submit" className="btn btn-primary">SUBMIT</button>
          </form>
        </div>
    )
  }
}

export default KonstituenCreate;
