import React, { Component } from 'react';
import Paragraph from './components/Paragraph';
import Title from './components/Title';

class App extends Component {
  constructor() {
    super()
    this.state = {
      text: 'Hello World of ReactJS',
      text2: 'Hello World of NodeJS',
      text3: {
        txt: 'Hello',
        txt2: 'World'
      }
    }
  }

  _handleClick = () => {
    this.setState({text2: 'Guys!'})
  }

  render() {
    return (
      <div>
        <Title/>
        <Paragraph></Paragraph>
        <p>{ this.state.text }</p>
        <p>{ this.state.text2 }</p>
        <p>{ this.state.text3.txt +' '+ this.state.text3.txt2}</p>
        <button onClick = { this._handleClick } >Click ME!</button>
      </div>
    );
  }
}

export default App;
