import React, { Component } from 'react';

class KonstituenCreate extends Component {
  constructor() {
    super()

    this.state = {
      nama : '',
      nik : '',
      alamat : '',
      kecamatan : '',
      kelurahan : '',
      tps : ''
    }
  }

  _handleChange = (e) => {
    this.setState({ [e.target.name] : e.target.value })
  }

  render() {
    return (
        <div>
          <h1>Tambah Konstituen</h1>
          <form>
            <div className="form-group">
              <label>Nama</label>
              <input 
                name="nama" 
                value = {this.state.nama} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Nomor Induk Kependudukan</label>
              <input 
                name="nik" 
                value = {this.state.nik} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Alamat</label>
              <input 
                name="alamat" 
                value = {this.state.alamat} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Kecamatan</label>
              <input 
                name="kecamatan" 
                value = {this.state.kecamatan} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Kelurahan</label>
              <input 
                name="kelurahan" 
                value = {this.state.kelurahan} 
                onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Nomor TPS</label>
              <input 
                name="tps" 
                value = {this.state.tps} onChange = { this._handleChange } 
                className="form-control"
              />
            </div>
            <button className="btn btn-primary">SUBMIT</button>
          < /form>
        </div>
    )
  }
}

export default KonstituenCreate;
