import React, { Component } from 'react';
import axios from 'axios';

class Konstituen extends Component {
   constructor() {
      super()
      this.state = {
         konstituens : []
         // konstituens : [
         //    {
         //       nama : 'Fickry',
         //       nik : '6112010710810009',
         //       alamat : 'Cempaka Mas',
         //       kecamatan : 1,
         //       kelurahan : 1,
         //       tps : 1
         //    }, {
         //       nama : 'Budi',
         //       nik : '72619856198257210',
         //       alamat : 'Jakarta',
         //       kecamatan : 3,
         //       kelurahan : 2,
         //       tps : 40
         //    }   
         // ]
      }
   }

   componentDidMount() {
      axios.get('http://localhost:5000/konstituens').then(res => {
         console.log(res)
         this.setState({ konstituens : res.data.data })
      }).catch(err => console.log(err))
   }

   render() {
      return (
         <div>
            <h1>Data Konstituen</h1>
            <table className="table">
               <thead>
                  <th>Nama</th>
                  <th>NIK</th>
                  <th>Alamat</th>
                  <th>Kecamatan</th>
                  <th>Kelurahan</th>
                  <th>Nomor TPS</th>
               </thead>
               <tbody>
                  {
                     this.state.konstituens.map((data, index) => {
                        return (
                           <tr key = { index }>
                              <td>{ data.nama }</td>
                              <td>{ data.nik }</td>
                              <td>{ data.alamat }</td>
                              <td>{ data.kecamatan }</td>
                              <td>{ data.kelurahan }</td>
                              <td>{ data.tps }</td>
                           </tr>
                        )
                     })
                  }
               </tbody>
            </table>
         </div>
      )
   }
}

export default Konstituen;
