import React, { Component } from 'react';

// DENGAN FUNCTION COMPONENT
const Table = (props) => {
   return (
      <table>
         <thead>
         <th>ID</th>
         <th>Nama</th>
         <th>Kelas</th>
         </thead>
         <tbody>
         {
            props.siswa.map((data, index) => {
               return (
               <tr key = {index}>
                  <td>{ data.id }</td>
                  <td>{ data.nama }</td>
                  <td>{ data.kelas }</td>
               </tr>
               )
            })
         }
         </tbody>
      </table>
   )
}

export default Table;
